import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthenticationContext";
import { doc, onSnapshot } from "firebase/firestore";
import { db } from "../Firebase";

const Chats = () => {
  const [chats, setChats] = useState([]); //it holds user chats from api
  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    const getChats = () => {
      const unsub = onSnapshot(doc(db, "userChats", currentUser.uid), (doc) => {
        setChats(doc.data);
      });
      //taken from firebase- "Get realtime updates with Cloud Firestore"
      return () => {
        unsub(); // this for clean up
      };
    };
    currentUser.uid && getChats();
  }, [currentUser.uid]);
  // it shld load in real time. so when there is changes in "userchats db" it will reflected here by using
  // "get real time data from firebase"

  console.log(chats);

  return (
    <div className="chats">
      <div className="user-chat">
        <img
          src="https://images.pexels.com/photos/39866/entrepreneur-startup-start-up-man-39866.jpeg?auto=compress&cs=tinysrgb&w=600"
          alt="chat-image"
        />
        <div className="userChat-info">
          <span className="name">Vinay</span>
          <span className="chat-info">Latest chat info</span>
        </div>
      </div>

      {/* ---------------------------------------------------------------------------------------- */}
      <div className="user-chat">
        <img
          src="https://images.pexels.com/photos/39866/entrepreneur-startup-start-up-man-39866.jpeg?auto=compress&cs=tinysrgb&w=600"
          alt="chat-image"
        />
        <div className="userChat-info">
          <span className="name">Vinay</span>
          <span className="chat-info">Latest chat info</span>
        </div>
      </div>

      {/* ----------------------------------------------------------------------------------------- */}

      <div className="user-chat">
        <img
          src="https://images.pexels.com/photos/39866/entrepreneur-startup-start-up-man-39866.jpeg?auto=compress&cs=tinysrgb&w=600"
          alt="chat-image"
        />
        <div className="userChat-info">
          <span className="name">Vinay</span>
          <span className="chat-info">Latest chat info</span>
        </div>
      </div>
    </div>
  );
};

export default Chats;
