import React, { useState } from 'react';
import { collection, query, where, getDocs } from "firebase/firestore";
import {db} from '../Firebase';

const Search = () => {

  const [username, setUsername] = useState("");  // used to change directly input entered by user
  const [user, setUser] = useState(null);   //if username is found, then that user is set to 'user' state
  const [error, setError] = useState(false);

  const handleSearch = async() => {
    const userRef = collection(db, "users");

    // Create a query against the collection.
    const q = query(userRef, where("displayName", "==", username));

    try{
      const querySnapshot = await getDocs(q);
      querySnapshot.forEach((doc) => {
      setUser(doc.data())
    });
    }catch(error){
      setError(true);
    }
  };

  const handleKey = (e) =>{
     e.code === "Enter" && handleSearch();
  };

  return (
    <div className='search'>
      <div className='search-form'>
        <input type='text' placeholder='Find the person' onKeyDown={handleKey} onChange={e=>setUsername(e.target.value)}></input>
      </div>
      {error && <span>User Not found</span>}
      {user && <div className='user-chat'>
        <img src={user.photoURL}
         alt='chat-image'/>
         <div className='userChat-info'>
            <span>{user.displayName}</span>
         </div>
      </div>}
    </div>
  )
}

export default Search;
